import React, { Component } from "react";
import Web3 from "web3";
import detectEthereumProvider from "@metamask/detect-provider";
import KryptoBird from '../abis/KryptoBird.json';

class App extends Component {

     async componentDidMount(){
         await this.loadWeb3();
     }

    // first up is to detect ethereum provider
    async loadWeb3() {
        const provider = await detectEthereumProvider();

        // modern browsers
        // if there is a provider then
        // lets log its working and access
        // the window from the doc to set
        // Web3 to the provider

        if(provider) {
            window.web3 = new Web3(provider);
            await this.loadBlockchainData();
        }else{
            // no ethereum provider
            window.alert('no ethereum wallet detected.');
        }
    }

    async loadBlockchainData(){
        const web3 = window.web3;
        await web3.eth.requestAccounts();
        const accounts = await web3.eth.getAccounts();
        this.setState({account: accounts});

        const networkId = await web3.eth.net.getId();
        const networkData = KryptoBird.networks[networkId]

        if(networkData) {
            const abi = KryptoBird.abi;
            const address = networkData.address;
            const contract = new web3.eth.Contract(abi, address);
            this.setState({contract});

            const totalSupply = await this.state.contract.methods.totalSupply().call();
            this.setState({totalSupply});

            for(let i = 0; i < totalSupply; i++){
                const KryptoBird = await contract.methods.KryptoBird(i).call();
                this.setState({
                    kryptoBirdz: [...this.state.kryptoBirdz, KryptoBird]
                });
            }

            console.log(this.state);
        }else {
            window.alert('Smart Contract not deployed.');
        }
    }

    mint = (kryptoBird) => {
        this.state.contract.methods.mint(kryptoBird).send({from: this.state.account}).once('Receipt', (Receipt) => {
            this.setState({
                kryptoBird: [...this.state.kryptoBirdz, KryptoBird]
            });
        });
    }

    constructor(props) {
        super(props);
        this.state = {
            account: '',
            contract: null,
            totalSupply: 0,
            kryptoBirdz: [],
        }
    }

    render() {
        return(
            <div>
                <nav className="navbar navbar-dark fixed-top bg-dark flex-md-nowrap p-0 shadow">
                    <div className="navbar-brand col-sm-3 col-md-3 mr-0" style={{color:'white'}}>
                        Krypto Birdz NFTS (Non Fungible Tokens)
                    </div>
                    <ul className="navbar-nav px-3">
                        <li className="nave-item text-nowrap d-none d-sm-none d-sm-block">
                            <small className="text-white">
                                {this.state.account}
                            </small>
                        </li>
                    </ul>
                </nav>

                <div className="container-fluid mt-1">
                    <div className="row">
                        <main role="main" className="col-lg-12 d-flex text-center">
                            <div className="content mr-auto ml-auto" style={{opacity:'0.8'}}>
                                <h1 style={{color:'white'}}>KryptoBirdz - NFT Marketplace</h1>
                                <form>

                                </form>
                            </div>
                        </main>
                    </div>
                </div>

            </div>
        )
    }
}

export default App;
